import { useEffect, useState } from 'react';
import {View, Text, ActivityIndicator, StyleSheet, FlatList, Button} from 'react-native';
import { getFavArray } from '../services/FavouritesManager';
import { getCurrUser } from '../services/LoginManager';
import FavouritesItem from '../components/FavouritesItem';

export default function Favourites({route, navigation}) {
    
    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [dataResult, setDataResult] = useState([]);

    const { currUser } = route.params;

    
    useEffect(() => {
        getFavArray(currUser)
        .then (
            (result) => {
                let arrFav = JSON.parse(result);  
                setIsLoaded(true);                  
                setDataResult(arrFav);  
            },
            (error) => {
                setIsLoaded(true);
                console.log('error: ' + error);
                setError(error);
            }
        ) 
        const willFocusSubscription = navigation.addListener('focus', () => {
        getFavArray(currUser)
        .then(
            (result) => {
            setIsLoaded(true);
            setDataResult(JSON.parse(result));
            },
            (error) => {
            setIsLoaded(true);
            setError(error);
            }
        )
        });

        return willFocusSubscription;

    })



    return(
        <View>
            <Text>My Favourites</Text>
            <Button
            title="Sync Favourites"
            buttonStyle={{backgroundColor:'#E16162'}}
            containerStyle={{
                width: 100,
                height: 30,
                marginTop: 10,
                marginTop: 0,
                marginBottom: 10,
                marginLeft: 'auto',
                marginRight: 'auto',
            }}
            titleStyle={{
                color: 'white',
                fontFamily: 'Roboto_400Regular',
                fontSize: 11,
            }}
            onPress={() => {syncFavourites(dataResult)}}
            />
            {displayRecipe(error, isLoaded, dataResult, navigation)}
        </View>
    )
}

function syncFavourites(arrFav) {

    //get favourites from api
    //get favourites from local storage
    //check which one is shorter
    //update if there are any differences
    fetch ('https://thoupapi.michellecheung.net/api/v1/users/getFaves.php')
    .then(res => res.json())
    .then(
        (result) => {

        },
        (error) => {

        }
    )
    
        // fetch('https://thoupapi.michellecheung.net/api/v1/users/create.php', {
        //     method: 'POST',
        //     headers: {
        //         'Content-Type': 'application/json',
        //     },
        //     body: JSON.stringify(arrFav) 
        // })
    
        
}



function displayRecipe(error, isLoaded, dataResult, navigation) {
    const renderItem = (item) => {
        <FavouritesItem itemData={item} navigatorRef={navigation}/> 
    }

    if (error) {
    // show an error message
        return (
            <View>
            <Text>Error: {error.message}</Text>
            </View>
        );
    }
    else if (!isLoaded) {
    // show the ActivityIndicator (spinner)
        return (
            <View>
                <Text>Preparing...</Text>
                <ActivityIndicator size="large" color="#ffffff"/>
            </View>
        );
    }
    else if (dataResult == null || dataResult == []) {
        
    // not an error but no meals, so show a message
        return (
            <View>
                <Text>No Favourites</Text>
            </View>
        );
    }
    else {
    // show the data in the flat list and display data on CategoryLayout.js
        return (
            <View style={styles.container}>
                <FlatList
                    data={dataResult}
                    renderItem={renderItem}
                    keyExtractor={item => item.id}
                />
            </View>
        );
    }

}

 const styles = StyleSheet.create({
   container: {
     flex: 1,
     backgroundColor: '#F6F0EE',
     alignItems: 'center',
     justifyContent: 'flex-start',
     width: '100%',
   },
   listContainer: {
    width: '100vw',
    backgroundColor: '#EFF0F3',
   },
   heading: {
    color: '#2C2A31',
    fontSize: 30,
    marginTop: 10,
    marginBottom: 10,
    fontFamily: 'Roboto_400Regular',
   }
 });
